import { Component, OnInit } from '@angular/core';
import { AuthService } from '../shared/auth.service';
import { Router } from '@angular/router';
import { ApiService } from '../shared/api.service';

@Component({
  selector: 'app-admin',
  templateUrl: './admin.component.html',
  styleUrls: ['./admin.component.css']
})
export class AdminComponent implements OnInit {
  newUser: any = {
    firstName: null,
    lastName: null,
    email: null,
    password: null,
    dob: null,
    gender: null,
    role: null,
  };
  updateUser: any = {
    firstName: null,
    lastName: null,
    email: null,
    password: null,
    dob: null,
    gender: null,
    role: null,
  };
  userList: any[];
  userListUi: any[];
  defaultSrc: string = 'data:image/png;base64,';
  constructor(private auth: AuthService, private api: ApiService, private route: Router) { }

  ngOnInit() {
    if (!this.auth.isLogin()) {
      this.route.navigate(["","login"]).then(res=>{
      }).catch(err=>{
      });
    }
    this.getAllUser();
  }

  /**
   * getAllUser
   */
  public getAllUser() {
    this.api.getUsers().then(resJson=>{
      console.log("AdminComponent: resJson ", resJson);
      if (resJson.data) {
        this.userList = resJson.data;
        this.filterUser();
      }
    }).catch(err=>{
      
    });
  }

  /**
   * addNewUser
   */
  public addNewUser(user: any) {
    let userData = {
        // "email": "manojkushwah@gmail.com",
        // "firstName": "Manoj",
        // "lastName": "kushwah",
        // "gender": "male",
        // "dob": "678306659000",
        // "role": "1"
    };
    userData = user;
    this.api.addUser(userData).then((res:any)=>{
      console.log("res: ", res);
      if(res.data){
        this.getAllUser();
        this.hideBsModal('#addNewUser');
        this.resetForm('#newUserForm');
        alert(res.message);
      } else{
        alert(res.message);
      }
    }).catch((err)=>{
      console.log("err: ", err);
    });
  }

  /**
   * deleteUser
   */
  public deleteUser(user: any) {
    let check = confirm("Are you sure?\ndelete this user.");
    if(!check){
      return;
    }
    let userData = {userId: user.userId};
    console.log('userData: ', userData);
    this.api.deleteUser(userData).then((res:any)=>{
      console.log('res: ', res);
      this.getAllUser();
    }).catch(err=>{
      console.log(err);
    })
  }


  /**
   * submitNewUser
   */
  public submitNewUser() {
    console.log('submitNewUser: ');
    console.log(this.newUser);

    if(this.newUser.firstName == null){
      // firstName error
    } else if(this.newUser.email == null){
      // Email error
    } else if(this.newUser.password == null){
      // Password error
    } else if(this.newUser.dob == null){
      // dob error
    } else if(this.newUser.gender == null){
      // gender error
    } else {
      this.addNewUser(this.newUser);
    }

    return false;
  }


  /**
   * updateUserInfo
   */
  public updateUserInfo(user: any) {
    let check = confirm("Are you sure?\nupdate this user info.");
    if(!check){
      return;
    }
    console.log('userData: ', user);
    this.api.updateUser(user).then((res:any)=>{
      console.log('res: ', res);
      this.getAllUser();
      this.hideBsModal('#updateUser');
      this.resetForm('#updateUserInfoForm');
    }).catch(err=>{
      console.log(err);
    })
  }

  /**
   * submitUpdateUserInfo
   */
  public submitUpdateUserInfo() {
    console.log('submitUpdateUserInfo: ');
    console.log(this.updateUser);

    if(this.updateUser.firstName == null){
      // firstName error
    } else if(this.updateUser.email == null){
      // Email error
    } else if(this.updateUser.password == null){
      // Password error
    } else if(this.updateUser.dob == null){
      // dob error
    } else if(this.updateUser.gender == null){
      // gender error
    } else {
      this.updateUserInfo(this.updateUser);
    }

    return false;
  }


  /**
   * openUpdateUserModal(user)
   */
  public openUpdateUserModal(user) {
    this.updateUser = user;
    this.showBsModal('#updateUser');
  }


  public filterUser(query?: string): void{
    this.userListUi = this.userList.filter(user => {
      return Boolean(!query || (query && String(user.firstName).concat(user.lastName,user.email).match(query)));
    });
  }

  /**
   * showBsModal
   */
  public showBsModal(id: string) {
    eval(`$('${id}').modal('show')`);
  }

  /**
   * hideBsModal
   */
  public hideBsModal(id: string) {
    eval(`$('${id}').modal('hide')`);
  }

  /**
   * resetForm
   */
  public resetForm(id: string) {
    eval(`$('${id}').trigger('reset')`);
  }

}
